import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import Navbar from '../components/Navbar';

const ProgramDetailPage = () => {
  const { programId } = useParams();
  const [program, setProgram] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchProgramDetails();
  }, [programId]);

  const fetchProgramDetails = async () => {
    try {
      setLoading(true);
      const response = await fetch(`/api/academic-programs/${programId}/`);
      if (!response.ok) {
        throw new Error('Failed to fetch program details');
      }
      const data = await response.json();
      setProgram(data);
    } catch (err) {
      setError(err.message);
      console.error('Error fetching program details:', err);
    } finally {
      setLoading(false);
    }
  };

  const getDegreeIcon = (degreeType) => {
    switch (degreeType) {
      case 'BS':
        return (
          <svg viewBox="0 0 24 24" width="48" height="48" fill="currentColor">
            <path d="M12 3L1 9l11 6 9-4.91V17h2V9L12 3zm0 13L3.74 11 12 6.82 20.26 11 12 16z"/>
          </svg>
        );
      case 'BA':
        return (
          <svg viewBox="0 0 24 24" width="48" height="48" fill="currentColor">
            <path d="M19 3H5c-1.1 0-2 .9-2 2v14c0 1.1.9 2 2 2h14c1.1 0 2-.9 2-2V5c-1.1-.9-2-2-2-2zm-5 14H7v-2h7v2zm3-4H7v-2h10v2zm0-4H7V7h10v2z"/>
          </svg>
        );
      case 'AA':
      case 'AS':
        return (
          <svg viewBox="0 0 24 24" width="48" height="48" fill="currentColor">
            <path d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z"/>
          </svg>
        );
      default:
        return (
          <svg viewBox="0 0 24 24" width="48" height="48" fill="currentColor">
            <path d="M12 2l3.09 6.26L22 9.27l-5 4.87 1.18 6.88L12 17.77l-6.18 3.25L7 14.14 2 9.27l6.91-1.01L12 2z"/>
          </svg>
        );
    }
  };

  const groupCoursesByYear = (courses) => {
    const grouped = {};
    courses.forEach(course => {
      if (!grouped[course.year_level]) {
        grouped[course.year_level] = { 1: [], 2: [] };
      }
      grouped[course.year_level][course.semester].push(course);
    });
    return grouped;
  };

  if (loading) {
    return (
      <div className="App">
        <Navbar />
        <div className="container" style={{ paddingTop: '100px', textAlign: 'center' }}>
          <h2>Loading Program Details...</h2>
          <div className="loading-spinner"></div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="App">
        <Navbar />
        <div className="container" style={{ paddingTop: '100px', textAlign: 'center' }}>
          <h2>Error Loading Program Details</h2>
          <p>{error}</p>
          <button onClick={fetchProgramDetails} className="btn btn-primary">
            Try Again
          </button>
          <Link to="/academics" className="btn btn-secondary" style={{ marginLeft: '10px' }}>
            Back to Programs
          </Link>
        </div>
      </div>
    );
  }

  if (!program) {
    return (
      <div className="App">
        <Navbar />
        <div className="container" style={{ paddingTop: '100px', textAlign: 'center' }}>
          <h2>Program Not Found</h2>
          <p>The requested academic program could not be found.</p>
          <Link to="/academics" className="btn btn-primary">
            Back to Programs
          </Link>
        </div>
      </div>
    );
  }

  const groupedCourses = groupCoursesByYear(program.courses || []);

  return (
    <div className="App">
      <Navbar />
      
      {/* Program Header */}
      <section className="section program-header">
        <div className="container">
          <div className="breadcrumb">
            <Link to="/">Home</Link> &gt; 
            <Link to="/academics">Academic Programs</Link> &gt; 
            <span>{program.name}</span>
          </div>
          
          <div className="program-hero">
            <div className="program-icon-large">
              {getDegreeIcon(program.degree_type)}
            </div>
            <div className="program-hero-content">
              <h1 className="program-title">{program.name}</h1>
              <p className="program-degree">{program.get_degree_type_display || program.degree_type}</p>
              <div className="program-meta">
                <span className="meta-item">
                  <strong>Duration:</strong> {program.duration}
                </span>
                <span className="meta-item">
                  <strong>Units Required:</strong> {program.units_required}
                </span>
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* Program Content */}
      <section className="section program-content">
        <div className="container">
          <div className="program-layout">
            {/* Main Content */}
            <div className="program-main">
              {/* Description */}
              <div className="program-section">
                <h2>Program Description</h2>
                <p>{program.description}</p>
              </div>

              {/* Specializations */}
              {program.specializations && program.specializations.length > 0 && (
                <div className="program-section">
                  <h2>Specializations</h2>
                  <div className="specializations-grid">
                    {program.specializations.map((spec, index) => (
                      <div key={index} className="specialization-card">
                        <h4>{spec.name}</h4>
                        <p>{spec.description}</p>
                      </div>
                    ))}
                  </div>
                </div>
              )}

              {/* Curriculum */}
              {program.courses && program.courses.length > 0 && (
                <div className="program-section">
                  <h2>Curriculum</h2>
                  <div className="curriculum-tabs">
                    {Object.keys(groupedCourses).sort().map(yearLevel => (
                      <div key={yearLevel} className="year-section">
                        <h3>{yearLevel} Year</h3>
                        <div className="semester-tabs">
                          {[1, 2].map(semester => (
                            <div key={semester} className="semester-section">
                              <h4>{semester} Semester</h4>
                              <div className="courses-list">
                                {groupedCourses[yearLevel][semester].map(course => (
                                  <div key={course.id} className="course-item">
                                    <div className="course-header">
                                      <span className="course-code">{course.code}</span>
                                      <span className="course-units">{course.units} units</span>
                                    </div>
                                    <h5 className="course-title">{course.title}</h5>
                                    <p className="course-description">{course.description}</p>
                                  </div>
                                ))}
                              </div>
                            </div>
                          ))}
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              )}
            </div>

            {/* Sidebar */}
            <div className="program-sidebar">
              <div className="sidebar-section">
                <h3>Program Information</h3>
                <ul className="program-info-list">
                  <li><strong>Degree:</strong> {program.get_degree_type_display || program.degree_type}</li>
                  <li><strong>Duration:</strong> {program.duration}</li>
                  <li><strong>Units Required:</strong> {program.units_required}</li>
                  <li><strong>Status:</strong> {program.is_active ? 'Active' : 'Inactive'}</li>
                </ul>
              </div>

              <div className="sidebar-section">
                <h3>Quick Actions</h3>
                <div className="action-buttons">
                  <button className="btn btn-primary btn-block">Apply Now</button>
                  <button className="btn btn-secondary btn-block">Download Brochure</button>
                  <button className="btn btn-outline btn-block">Contact Advisor</button>
                </div>
              </div>

              <div className="sidebar-section">
                <h3>Related Programs</h3>
                <div className="related-programs">
                  <p>Explore other academic programs at CCB:</p>
                  <Link to="/academics" className="btn btn-outline btn-block">
                    View All Programs
                  </Link>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* Footer */}
      <footer className="footer">
        <div className="container">
          <div className="footer-content">
            <div className="footer-section">
              <h3>City College of Bayawan</h3>
              <p>Honor and Excellence for the Highest Good</p>
              <p>Honus et Excellentia Ad Summum Bonum</p>
            </div>
            <div className="footer-section">
              <h4>Contact Information</h4>
              <p>📍 Bayawan City, Negros Oriental</p>
              <p>📞 (035) XXX-XXXX</p>
              <p>✉️ info@ccb.edu.ph</p>
            </div>
            <div className="footer-section">
              <h4>Quick Links</h4>
              <Link to="/">Home</Link>
              <Link to="/academics">Academic Programs</Link>
              <a href="#admissions">Admissions</a>
              <a href="#news">News & Events</a>
            </div>
          </div>
          <div className="footer-bottom">
            <p>&copy; 2025 City College of Bayawan. All rights reserved.</p>
          </div>
        </div>
      </footer>
    </div>
  );
};

export default ProgramDetailPage;
