import React, { useState } from 'react';
import SearchModal from './SearchModal';
import apiService from '../services/api';
import './Navbar.css';

const Navbar = ({ isTopBarVisible }) => {
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isSearchModalOpen, setIsSearchModalOpen] = useState(false);
  const [user, setUser] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  const toggleSearchModal = () => {
    setIsSearchModalOpen(!isSearchModalOpen);
  };

  const handleLogin = async () => {
    if (isLoggedIn) {
      // Handle logout
      try {
        await apiService.logout();
        setIsLoggedIn(false);
        setUser(null);
      } catch (error) {
        console.error('Logout failed:', error);
      }
    } else {
      // Handle login - for demo purposes, we'll use a simple prompt
      const username = prompt('Enter username:');
      const password = prompt('Enter password:');

      if (username && password) {
        setIsLoading(true);
        try {
          const response = await apiService.login({ username, password });
          if (response.status === 'success') {
            setIsLoggedIn(true);
            setUser(response.user);
          } else {
            alert('Login failed: ' + response.message);
          }
        } catch (error) {
          console.error('Login failed:', error);
          alert('Login failed. Please try again.');
        } finally {
          setIsLoading(false);
        }
      }
    }
  };

  // Smooth scroll function
  const handleNavClick = (e, targetId) => {
    e.preventDefault();

    // Close mobile menu if open
    if (isMobileMenuOpen) {
      setIsMobileMenuOpen(false);
    }

    const targetElement = document.getElementById(targetId);
    if (targetElement) {
      const navbarHeight = document.querySelector('.navbar').offsetHeight;
      const targetPosition = targetElement.offsetTop - navbarHeight;

      window.scrollTo({
        top: targetPosition,
        behavior: 'smooth'
      });
    }
  };

  // Handle top bar link clicks
  const handleTopBarClick = async (e, section) => {
    e.preventDefault();

    try {
      let data;
      switch (section) {
        case 'students':
          data = await apiService.getStudentsSection();
          console.log('Students section data:', data);
          // You can show this data in a modal or navigate to a dedicated page
          alert(`Students Portal\nQuick Links: ${data.quick_links.length}\nAnnouncements: ${data.announcements.length}`);
          break;
        case 'faculty':
          data = await apiService.getFacultySection();
          console.log('Faculty section data:', data);
          alert(`Faculty Portal\nQuick Links: ${data.quick_links.length}\nDepartments: ${data.departments.length}`);
          break;
        case 'about':
          data = await apiService.getAboutSection();
          console.log('About section data:', data);
          alert(`About CCB\nMission: ${data.mission.substring(0, 100)}...\nStudents: ${data.stats.students}`);
          break;
        case 'contact':
          data = await apiService.getContactSection();
          console.log('Contact section data:', data);
          alert(`Contact Information\nPhone: ${data.main_office.phone}\nEmail: ${data.main_office.email}`);
          break;
        default:
          break;
      }
    } catch (error) {
      console.error(`Error fetching ${section} data:`, error);
      alert(`Unable to load ${section} information. Please try again.`);
    }
  };

  // Get current date
  const getCurrentDate = () => {
    const today = new Date();
    const options = { 
      weekday: 'long', 
      year: 'numeric', 
      month: 'long', 
      day: 'numeric' 
    };
    return today.toLocaleDateString('en-US', options);
  };

  return (
    <>
      <nav className="navbar">
        {/* Top Orange Bar */}
        <div className={`top-bar ${isTopBarVisible ? 'top-bar-visible' : 'top-bar-hidden'}`}>
