                <span className="separator">|</span>
                <a href="#about" className="top-link" onClick={(e) => handleTopBarClick(e, 'about')}>ABOUT US</a>
                <span className="separator">|</span>
                <a href="#contact" className="top-link" onClick={(e) => handleTopBarClick(e, 'contact')}>CONTACT US</a>
