import React, { useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import HomePage from './HomePage';
import AcademicPrograms from './academicprogram';

function App() {
  // Force page reset on refresh/reload
  useEffect(() => {
    // Scroll to top immediately
    window.scrollTo(0, 0);

    // Force a complete page reset
    if (window.performance && window.performance.navigation.type === window.performance.navigation.TYPE_RELOAD) {
      // This is a page refresh/reload
      window.location.reload(true); // Force reload from server, not cache
    }
  }, []);

  return (
    <Router>
      <Routes>
        <Route path="/" element={<HomePage />} />
        <Route path="/academics" element={<AcademicPrograms />} />
        <Route path="/admissions" element={<HomePage />} />
        <Route path="/news" element={<HomePage />} />
        <Route path="/downloads" element={<HomePage />} />
      </Routes>
    </Router>
  );
}

export default App;
