import React, { useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';
import HomePage from './HomePage';
import AcademicPrograms from './academicprogram';

// Component to handle scroll to top on route changes
function ScrollToTop() {
  const { pathname } = useLocation();

  useEffect(() => {
    // Scroll to top without animation when route changes
    window.scrollTo(0, 0);
  }, [pathname]);

  return null;
}

function App() {
  return (
    <Router>
      <ScrollToTop />
      <Routes>
        <Route path="/" element={<HomePage />} />
        <Route path="/academics" element={<AcademicPrograms />} />
        <Route path="/admissions" element={<HomePage />} />
        <Route path="/news" element={<HomePage />} />
        <Route path="/downloads" element={<HomePage />} />
      </Routes>
    </Router>
  );
}

export default App;
