import React, { useState, useEffect } from 'react';
import './Navbar.css';

const Navbar = ({ isTopBarVisible }) => {
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [activeSection, setActiveSection] = useState('home');

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  const toggleLoginStatus = () => {
    setIsLoggedIn(!isLoggedIn);
  };

  // Smooth scroll function with section tracking
  const handleNavClick = (e, targetId) => {
    e.preventDefault();

    // Close mobile menu if open
    if (isMobileMenuOpen) {
      setIsMobileMenuOpen(false);
    }

    const targetElement = document.getElementById(targetId);
    if (targetElement) {
      const navbarHeight = document.querySelector('.navbar').offsetHeight;
      const targetPosition = targetElement.offsetTop - navbarHeight;

      window.scrollTo({
        top: targetPosition,
        behavior: 'smooth'
      });
    }
  };

  // Track which section is currently in view for highlighting
  useEffect(() => {
    const handleScroll = () => {
      const sections = ['home', 'academics', 'admissions', 'news', 'downloads'];
      const navbarHeight = document.querySelector('.navbar')?.offsetHeight || 0;

      for (let i = sections.length - 1; i >= 0; i--) {
        const section = document.getElementById(sections[i]);
        if (section) {
          const rect = section.getBoundingClientRect();
          if (rect.top <= navbarHeight + 100) { // 100px threshold
            setActiveSection(sections[i]);
            break;
          }
        }
      }
    };

    window.addEventListener('scroll', handleScroll, { passive: true });
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Get current date
  const getCurrentDate = () => {
    const today = new Date();
    const options = { 
      weekday: 'long', 
      year: 'numeric', 
      month: 'long', 
      day: 'numeric' 
    };
    return today.toLocaleDateString('en-US', options);
  };

  // Determine which navigation item should be active based on current section
  const getActiveNavClass = (sectionId) => {
    return activeSection === sectionId ? 'nav-link active-nav' : 'nav-link';
  };

  return (
    <nav className="navbar">
      {/* Top Orange Bar */}
      <div className={`top-bar ${isTopBarVisible ? 'top-bar-visible' : 'top-bar-hidden'}`}>
        <div className="top-bar-container">
          {/* Left spacer (keeps center links centered) */}
          <div className="top-bar-left"></div>

          <div className="top-bar-center">
            <div className="top-bar-links">
              <a href="#students" className="top-link" onClick={(e) => handleNavClick(e, 'students')}>STUDENTS</a>
              <span className="separator">|</span>
              <a href="#faculty" className="top-link" onClick={(e) => handleNavClick(e, 'faculty')}>FACULTY & STAFF</a>
              <span className="separator">|</span>
              <a href="#about" className="top-link" onClick={(e) => handleNavClick(e, 'about')}>ABOUT US</a>
              <span className="separator">|</span>
              <a href="#contact" className="top-link" onClick={(e) => handleNavClick(e, 'contact')}>CONTACT US</a>
            </div>
          </div>

          <div className="top-bar-right">
            <span className="date-text">Today is {getCurrentDate()}</span>
            <div className="login-section" onClick={toggleLoginStatus}>
              <div className="user-icon">
                <svg width="16" height="16" viewBox="0 0 24 24" fill="currentColor" aria-hidden="true">
                  <path d="M12 12c2.21 0 4-1.79 4-4s-1.79-4-4-4-4 1.79-4 4 1.79 4 4 4zm0 2c-2.67 0-8 1.34-8 4v2h16v-2c0-2.66-5.33-4-8-4z"/>
                </svg>
              </div>
              <span className="login-text">
                {isLoggedIn ? 'LOG OUT' : 'LOG IN'}
              </span>
            </div>
          </div>
        </div>
      </div>

      {/* Main Green Navigation Bar */}
      <div className={`main-nav ${!isTopBarVisible ? 'expanded' : ''}`}>
        <div className="nav-container">
          {/* Logo and Brand */}
          <div className="brand">
            <div className="logo">
              <img src="/images/ccb-logo.png" alt="City College of Bayawan logo" className="brand-logo" />
            </div>
            <div className="brand-text">
              <h1>CITY COLLEGE</h1>
              <h2>OF BAYAWAN</h2>
            </div>
          </div>

          {/* Mobile Menu Button */}
          <button className="mobile-menu-btn" onClick={toggleMobileMenu} aria-label="Toggle navigation menu">
            <span></span>
            <span></span>
            <span></span>
          </button>

          {/* Navigation Links */}
