import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Navbar from '../components/Navbar';

const AcademicProgramsPage = () => {
  const [programs, setPrograms] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchAcademicPrograms();
  }, []);

  const fetchAcademicPrograms = async () => {
    try {
      setLoading(true);
      const response = await fetch('/api/academic-programs/');
      if (!response.ok) {
        throw new Error('Failed to fetch academic programs');
      }
      const data = await response.json();
      setPrograms(data);
    } catch (err) {
      setError(err.message);
      console.error('Error fetching academic programs:', err);
    } finally {
      setLoading(false);
    }
  };

  const getDegreeIcon = (degreeType) => {
    switch (degreeType) {
      case 'BS':
        return (
          <svg viewBox="0 0 24 24" width="48" height="48" fill="currentColor">
            <path d="M12 3L1 9l11 6 9-4.91V17h2V9L12 3zm0 13L3.74 11 12 6.82 20.26 11 12 16z"/>
          </svg>
        );
      case 'BA':
        return (
          <svg viewBox="0 0 24 24" width="48" height="48" fill="currentColor">
            <path d="M19 3H5c-1.1 0-2 .9-2 2v14c0 1.1.9 2 2 2h14c1.1 0 2-.9 2-2V5c-1.1-.9-2-2-2-2zm-5 14H7v-2h7v2zm3-4H7v-2h10v2zm0-4H7V7h10v2z"/>
          </svg>
        );
      case 'AA':
      case 'AS':
        return (
          <svg viewBox="0 0 24 24" width="48" height="48" fill="currentColor">
            <path d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z"/>
          </svg>
        );
      default:
        return (
          <svg viewBox="0 0 24 24" width="48" height="48" fill="currentColor">
            <path d="M12 2l3.09 6.26L22 9.27l-5 4.87 1.18 6.88L12 17.77l-6.18 3.25L7 14.14 2 9.27l6.91-1.01L12 2z"/>
          </svg>
        );
    }
  };

  if (loading) {
    return (
      <div className="App">
        <Navbar />
        <div className="container" style={{ paddingTop: '100px', textAlign: 'center' }}>
          <h2>Loading Academic Programs...</h2>
          <div className="loading-spinner"></div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="App">
        <Navbar />
        <div className="container" style={{ paddingTop: '100px', textAlign: 'center' }}>
          <h2>Error Loading Academic Programs</h2>
          <p>{error}</p>
          <button onClick={fetchAcademicPrograms} className="btn btn-primary">
            Try Again
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="App">
      <Navbar />
      
      {/* Academic Programs Header */}
      <section className="section academics-header">
        <div className="container">
          <h1 className="page-title">Academic Programs</h1>
          <p className="page-subtitle">
            Discover our comprehensive range of academic programs designed to prepare you for success
          </p>
        </div>
      </section>

      {/* Academic Programs List */}
      <section className="section academics-content">
        <div className="container">
          {programs.length === 0 ? (
            <div className="no-programs">
              <h3>No Academic Programs Available</h3>
              <p>Please check back later or contact the administration for more information.</p>
            </div>
          ) : (
            <div className="programs-grid-detailed">
              {programs.map((program) => (
                <div key={program.id} className="program-card-detailed">
                  <div className="program-header">
                    <div className="program-icon">
                      {getDegreeIcon(program.degree_type)}
                    </div>
                    <div className="program-info">
                      <h3>{program.name}</h3>
                      <p className="degree-type">{program.get_degree_type_display || program.degree_type}</p>
                      <p className="duration">Duration: {program.duration}</p>
                      <p className="units">Units Required: {program.units_required}</p>
                    </div>
                  </div>
                  
                  <div className="program-description">
                    <p>{program.description}</p>
                  </div>

                  {program.specializations && program.specializations.length > 0 && (
                    <div className="program-specializations">
                      <h4>Specializations:</h4>
                      <ul>
                        {program.specializations.map((spec, index) => (
                          <li key={index}>{spec.name}</li>
                        ))}
                      </ul>
                    </div>
                  )}

                  <div className="program-actions">
                    <Link to={`/academics/${program.id}`} className="btn btn-primary">
                      View Details
                    </Link>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </section>

      {/* Footer */}
      <footer className="footer">
        <div className="container">
          <div className="footer-content">
            <div className="footer-section">
              <h3>City College of Bayawan</h3>
              <p>Honor and Excellence for the Highest Good</p>
              <p>Honus et Excellentia Ad Summum Bonum</p>
            </div>
            <div className="footer-section">
              <h4>Contact Information</h4>
              <p>📍 Bayawan City, Negros Oriental</p>
              <p>📞 (035) XXX-XXXX</p>
              <p>✉️ info@ccb.edu.ph</p>
            </div>
            <div className="footer-section">
              <h4>Quick Links</h4>
              <Link to="/">Home</Link>
              <Link to="/academics">Academic Programs</Link>
              <a href="#admissions">Admissions</a>
              <a href="#news">News & Events</a>
            </div>
          </div>
          <div className="footer-bottom">
            <p>&copy; 2025 City College of Bayawan. All rights reserved.</p>
          </div>
        </div>
      </footer>
    </div>
  );
};

export default AcademicProgramsPage;
