import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './Navbar.css';

const Navbar = ({ isTopBarVisible }) => {
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [activeNav, setActiveNav] = useState('home');
  const location = useLocation();
  const navigate = useNavigate();

  // Update active navigation based on current route
  useEffect(() => {
    const path = location.pathname;
    if (path === '/') {
      setActiveNav('home');
    } else if (path === '/academics') {
      setActiveNav('academics');
    } else if (path === '/admissions') {
      setActiveNav('admissions');
    } else if (path === '/news') {
      setActiveNav('news');
    } else if (path === '/downloads') {
      setActiveNav('downloads');
    }
  }, [location]);

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  const toggleLoginStatus = () => {
    setIsLoggedIn(!isLoggedIn);
  };

  // Navigation handler for React Router
  const handleNavClick = (navItem) => {
    setActiveNav(navItem);

    // Close mobile menu if open
    if (isMobileMenuOpen) {
      setIsMobileMenuOpen(false);
    }

    // Navigate to appropriate route
    switch (navItem) {
      case 'home':
        navigate('/');
        break;
      case 'academics':
        navigate('/academics');
        break;
      case 'admissions':
        navigate('/admissions');
        break;
      case 'news':
        navigate('/news');
        break;
      case 'downloads':
        navigate('/downloads');
        break;
      default:
        navigate('/');
    }
  };

  // Smooth scroll function for top bar links
  const handleTopBarClick = (e, targetId) => {
    e.preventDefault();

    const targetElement = document.getElementById(targetId);
    if (targetElement) {
      const navbarHeight = document.querySelector('.navbar').offsetHeight;
      const targetPosition = targetElement.offsetTop - navbarHeight;

      window.scrollTo({
        top: targetPosition,
        behavior: 'smooth'
      });
    }
  };

  // Get current date
  const getCurrentDate = () => {
    const today = new Date();
    const options = { 
      weekday: 'long', 
      year: 'numeric', 
      month: 'long', 
      day: 'numeric' 
    };
    return today.toLocaleDateString('en-US', options);
  };

  return (
    <nav className="navbar">
      {/* Top Orange Bar */}
      <div className={`top-bar ${isTopBarVisible ? 'top-bar-visible' : 'top-bar-hidden'}`}>
        <div className="top-bar-container">
          {/* Left spacer (keeps center links centered) */}
          <div className="top-bar-left"></div>

          <div className="top-bar-center">
            <div className="top-bar-links">
              <a href="#students" className="top-link" onClick={(e) => handleTopBarClick(e, 'students')}>STUDENTS</a>
              <span className="separator">|</span>
              <a href="#faculty" className="top-link" onClick={(e) => handleTopBarClick(e, 'faculty')}>FACULTY & STAFF</a>
              <span className="separator">|</span>
              <a href="#about" className="top-link" onClick={(e) => handleTopBarClick(e, 'about')}>ABOUT US</a>
              <span className="separator">|</span>
              <a href="#contact" className="top-link" onClick={(e) => handleTopBarClick(e, 'contact')}>CONTACT US</a>
            </div>
          </div>

          <div className="top-bar-right">
            <span className="date-text">Today is {getCurrentDate()}</span>
            <div className="login-section" onClick={toggleLoginStatus}>
              <div className="user-icon">
                <svg width="16" height="16" viewBox="0 0 24 24" fill="currentColor" aria-hidden="true">
                  <path d="M12 12c2.21 0 4-1.79 4-4s-1.79-4-4-4-4 1.79-4 4 1.79 4 4 4zm0 2c-2.67 0-8 1.34-8 4v2h16v-2c0-2.66-5.33-4-8-4z"/>
                </svg>
              </div>
              <span className="login-text">
                {isLoggedIn ? 'LOG OUT' : 'LOG IN'}
              </span>
            </div>
          </div>
        </div>
      </div>

      {/* Main Green Navigation Bar */}
      <div className={`main-nav ${!isTopBarVisible ? 'expanded' : ''}`}>
        <div className="nav-container">
          {/* Logo and Brand */}
          <div className="brand">
            <div className="logo">
