import React, { useState, useEffect, useRef } from 'react';
import apiService from '../services/api';
import './SearchModal.css';

const SearchModal = ({ isOpen, onClose }) => {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const searchInputRef = useRef(null);

  useEffect(() => {
    if (isOpen && searchInputRef.current) {
      searchInputRef.current.focus();
    }
  }, [isOpen]);

  useEffect(() => {
    const handleEscape = (e) => {
      if (e.key === 'Escape') {
        onClose();
      }
    };

    if (isOpen) {
      document.addEventListener('keydown', handleEscape);
      document.body.style.overflow = 'hidden';
    }

    return () => {
      document.removeEventListener('keydown', handleEscape);
      document.body.style.overflow = 'unset';
    };
  }, [isOpen, onClose]);

  const handleSearch = async (searchQuery) => {
    if (!searchQuery.trim()) {
      setResults([]);
      return;
    }

    setLoading(true);
    setError(null);

    try {
      const response = await apiService.search(searchQuery);
      setResults(response.results || []);
    } catch (err) {
      setError('Search failed. Please try again.');
      setResults([]);
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const value = e.target.value;
    setQuery(value);
    
    // Debounce search
    clearTimeout(window.searchTimeout);
    window.searchTimeout = setTimeout(() => {
      handleSearch(value);
    }, 300);
  };

  const handleResultClick = (result) => {
    // Navigate to the result
    const element = document.querySelector(result.url);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
    onClose();
  };

  const getResultIcon = (type) => {
    switch (type) {
      case 'program':
        return '🎓';
      case 'news':
        return '📰';
      case 'form':
        return '📄';
      case 'policy':
        return '📋';
      default:
        return '🔍';
    }
  };

  if (!isOpen) return null;

  return (
    <div className="search-modal-overlay" onClick={onClose}>
      <div className="search-modal" onClick={(e) => e.stopPropagation()}>
        <div className="search-modal-header">
          <h2>Search CCB Portal</h2>
          <button className="search-modal-close" onClick={onClose}>
            <svg viewBox="0 0 24 24" width="24" height="24" fill="currentColor">
              <path d="M19 6.41L17.59 5 12 10.59 6.41 5 5 6.41 10.59 12 5 17.59 6.41 19 12 13.41 17.59 19 19 17.59 13.41 12z"/>
            </svg>
          </button>
        </div>

        <div className="search-modal-content">
          <div className="search-input-container">
            <input
              ref={searchInputRef}
              type="text"
              className="search-modal-input"
              placeholder="Search for programs, news, forms, policies..."
              value={query}
              onChange={handleInputChange}
            />
            {loading && (
              <div className="search-loading">
                <div className="search-spinner"></div>
              </div>
            )}
          </div>

          {error && (
            <div className="search-error">
              <p>{error}</p>
            </div>
          )}

          <div className="search-results">
            {results.length > 0 ? (
              <>
                <div className="search-results-header">
                  <p>Found {results.length} result{results.length !== 1 ? 's' : ''}</p>
                </div>
                <div className="search-results-list">
                  {results.map((result, index) => (
                    <div
                      key={index}
                      className="search-result-item"
                      onClick={() => handleResultClick(result)}
                    >
                      <div className="search-result-icon">
                        {getResultIcon(result.type)}
                      </div>
                      <div className="search-result-content">
                        <h4>{result.title}</h4>
                        <p className="search-result-type">{result.type}</p>
                      </div>
                    </div>
                  ))}
                </div>
              </>
            ) : query && !loading ? (
              <div className="search-no-results">
                <p>No results found for "{query}"</p>
                <p>Try different keywords or check your spelling</p>
              </div>
            ) : null}
          </div>
        </div>
      </div>
    </div>
  );
};

export default SearchModal;
